var buyItems=document.querySelectorAll(".buy"),modalMessage=document.querySelector(".item-added-popup"),closebtn=modalMessage.querySelector(".close-button");buyItems.forEach(function(e){e.addEventListener("click",function(e){e.preventDefault(),modalMessage.classList.add("modal-show")})}),closebtn.addEventListener("click",function(e){e.preventDefault(),modalMessage.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&modalMessage.classList.contains("modal-show")&&(e.preventDefault(),modalMessage.classList.remove("modal-show"),modalMessage.classList.remove("modal-error"))});